const inputData = $input.item.json.bozuk_json_icerigi;

if (!inputData || inputData.trim() === '' || inputData.trim() === 'BOZUK .JSON DOSYASININ TÜM İÇERİĞİNİ BU ALANA YAPIŞTIRIN') {
  throw new Error('Lütfen bir önceki `Set` noduna düzeltilecek JSON içeriğini yapıştırın.');
}

let workflowJson;

// 1. Dış katmanı (rawJson) temizle
try {
  const parsedInput = JSON.parse(inputData);
  if (Array.isArray(parsedInput) && parsedInput[0] && parsedInput[0].rawJson) {
    workflowJson = JSON.parse(parsedInput[0].rawJson);
  } else {
    // Eğer rawJson formatında değilse, belki de zaten temizlenmiş. Olduğu gibi al.
    workflowJson = parsedInput;
  }
} catch (e) {
  throw new Error(`Girdi metni geçerli bir JSON değil gibi görünüyor. Lütfen dosya içeriğini tam olarak kopyaladığınızdan emin olun. Hata: ${e.message}`);
}


// 2. Hatalı ifadeleri (expression) düzelt
for (const node of workflowJson.nodes) {
  if (node.type === 'n8n-nodes-base.httpRequest' && 
      node.parameters && 
      typeof node.parameters.jsonBody === 'string' && 
      node.parameters.jsonBody.startsWith('={')) {

    // '={...}' formatını temizleyip '{...}' haline getir
    node.parameters.jsonBody = node.parameters.jsonBody.substring(2, node.parameters.jsonBody.length - 1);
  }
}

return {
  duzeltilmis_workflow_json: workflowJson
};
