// Log items to understand structure
console.log('Items length:', items.length);
items.forEach((item, index) => {
  console.log(`Item ${index} keys:`, Object.keys(item.json || {}));
});

// Safe property accessor function
function safeGet(obj, path, defaultValue = null) {
  try {
    const result = path.split('.').reduce((o, key) => (o && o[key] !== undefined) ? o[key] : undefined, obj);
    return result !== undefined ? result : defaultValue;
  } catch (error) {
    return defaultValue;
  }
}

// Extract data with safety checks
const scene_titles = items.map(item => 
  safeGet(item, 'json.response.text') || 
  safeGet(item, 'json.title') || 
  safeGet(item, 'json.message.content') || 
  null
).filter(Boolean);

// Handle output array or direct video_url
const video_urls = items.map(item => {
  const output = safeGet(item, 'json.output');
  if (Array.isArray(output) && output.length > 0) {
    return output[0]; // Get first element if it's an array
  }
  return safeGet(item, 'json.data.output.video_url') || null;
}).filter(Boolean);

const sound_urls = items.map(item => 
  safeGet(item, 'json.webContentLink') || 
  safeGet(item, 'json.data.webContentLink') || 
  null
).filter(Boolean);

// Log what we found
console.log('Found scene titles:', scene_titles.length);
console.log('Found video URLs:', video_urls.length);
console.log('Found sound URLs:', sound_urls.length);

// Return the combined result
return [{
  scene_titles,
  video_urls,
  sound_urls
}];

// First, log what we're receiving to understand the structure
console.log("Items received:", JSON.stringify(items, null, 2));

// Get the text from the input data, handling various possible structures
function getInputText() {
  // If items is an array
  if (Array.isArray(items)) {
    console.log("Input is an array with length:", items.length);

    // If it's an array with text properties
    if (items.length > 0 && items.some(item => item.text)) {
      return items.map(item => item.text || "").join("\n");
    }

    // If first item contains the data
    if (items.length > 0 && typeof items[0] === 'object') {
      // Try to find text in the first item's properties
      for (const key of Object.keys(items[0])) {
        if (typeof items[0][key] === 'string') {
          console.log(`Found string in items[0].${key}`);
          return items[0][key];
        }
      }
    }
  }

  // If items is a single object with a text property
  if (items && typeof items === 'object' && items.text) {
    console.log("Input is an object with text property");
    return items.text;
  }

  // If items itself is a string
  if (typeof items === 'string') {
    console.log("Input is a string");
    return items;
  }

  // If we have json.text (common n8n pattern)
  if (items && items.json && items.json.text) {
    console.log("Found text in items.json.text");
    return items.json.text;
  }

  console.log("Could not find text in the input, returning JSON string of input");
  return JSON.stringify(items);
}

// This function works with a single text string input
function extractScenesFromText(inputText) {
  try {
    console.log("Processing input text, length:", inputText.length);
    console.log("First 100 chars:", inputText.substring(0, 100));

    // Split the text by scene intro markers
    const sceneSections = inputText.split("**SCENE 1: INTRO**");

    // The first element will be empty or contain text before the first scene
    if (!sceneSections[0].trim()) {
      sceneSections.shift();
    }

    console.log(`Found ${sceneSections.length} potential scenes`);

    // Process each section into a structured scene object
    const scenes = sceneSections.map((section, index) => {
      // Remove any SCENE 2 markers
      section = section.replace("**SCENE 2: FIRST SECTION**", "").trim();

      // Extract the title, description, and audio using simpler approach
      const lines = section.split("- ");
      let title = "", description = "", audio = "";

      for (const line of lines) {
        if (line.startsWith("TITLE:")) {
          title = line.replace("TITLE:", "").trim();
        } else if (line.startsWith("DESCRIPTION:")) {
          description = line.replace("DESCRIPTION:", "").trim();
        } else if (line.startsWith("AUDIO:")) {
          audio = line.replace("AUDIO:", "").trim();
        }
      }

      console.log(`Scene ${index + 1}: Title="${title.substring(0, 20)}..."`);

      return {
        type: "intro",
        number: 1,
        title,
        description,
        audio
      };
    });

    // Filter out incomplete scenes
    const validScenes = scenes.filter(scene => 
      scene.title && scene.description && scene.audio
    );

    console.log(`Final count: ${validScenes.length} complete scenes`);
    return validScenes;
  }
  catch (error) {
    console.error("Error processing scenes:", error);
    return [{ error: error.message }]; // Return error info instead of empty array
  }
}

// Get the input text from whatever format it's in
const inputText = getInputText();

// Process the text and return the scenes
const extractedScenes = extractScenesFromText(inputText);

// Make sure we return something even if there are no scenes
if (extractedScenes.length === 0) {
  console.log("No scenes extracted, returning error info");
  return [{ 
    error: "No valid scenes could be extracted", 
    inputSample: inputText.substring(0, 200) 
  }];
}

console.log("Successfully returning scenes");
return extractedScenes;

